    public int countCG(String dna) {
        int count = 0;
        int startIndex = 0;
        
        while (true) {
            // Find the next occurrence of 'C' or 'G' after startIndex
            int cgIndex = dna.indexOf('C', startIndex);
            int gIndex = dna.indexOf('G', startIndex);
            
            // Check if either 'C' or 'G' was found
            if (cgIndex == -1 && gIndex == -1) {
                break;
            }
            
            // Determine the index of the next occurrence
            int nextIndex;
            if (cgIndex == -1) {
                nextIndex = gIndex;
            } else if (gIndex == -1) {
                nextIndex = cgIndex;
            } else {
                nextIndex = Math.min(cgIndex, gIndex);
            }
            
            // Increment the count
            count++;
            
            // Update startIndex for the next iteration
            startIndex = nextIndex + 1;
        }
        
        return count;
    }    
    
