    public int countCTG(String dna) {
        int count = 0;
        int startIndex = 0;
        
        while (true) {
            // Find the next occurrence of "CTG" after startIndex
            int ctgIndex = dna.indexOf("CTG", startIndex);
            
            // Check if "CTG" was found
            if (ctgIndex == -1) {
                break;
            }
            
            // Increment the count
            count++;
            
            // Update startIndex for the next iteration
            startIndex = ctgIndex + 3; // Move startIndex to the next position after "CTG"
        }
        
        return count;
    }
    
