    public String mystery(String dna) {
      int pos = dna.indexOf("T");
      int count = 0;
      int startPos = 0;
      String newDna = "";
      if (pos == -1) {
        return dna;
      }
      while (count < 3) {
        count += 1;
        newDna = newDna + dna.substring(startPos,pos);
        startPos = pos+1;
        pos = dna.indexOf("T", startPos);
        if (pos == -1) {
          break;
        }
      }
      newDna = newDna + dna.substring(startPos);
      return newDna;
    }
    
    
    public void testMystery (){
    String dna = "ATGCTAAGCTAAGCTAAGCTAG";
    String mystery = mystery(dna);
    System.out.println("Original dna is ATGCTAAGCTAAGCTAAGCTAG and mystery dna is" + mystery);
    }
